import React, { useState } from 'react';
import Navbar from './navbar';



const sliderImages = [
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTfj-v2PYnqG-Aai-XZENh0rdfe1-56htvVsQ&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR6lZYtoz3tOTJqxOv3qJWnG50bKsZOxAgRPA&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQuWUDX0dzBffH2_5r-lh9lrdbcRUQd12yKBQ&s",
];

const products = [
  {
    name: 'Vitamin C Serum',
    price: '$29.99',
    image: 'data:image/jpeg;base64,',
  },
  {
    name: 'Hydrating Face Cream',
    price: '$19.99',
    image: 'data:image/jpeg;base64,',
  },
  {
    name: 'Night Repair Oil',
    price: '$34.99',
    image: 'data:image/jpeg;base64,',
  },
  {
    name: 'Acne Spot Treatment',
    price: '$14.99',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQD3W4RzHGJIEV3Fv8wA9ay9WkiwYzXZMYusw&s',
  },
];

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % sliderImages.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + sliderImages.length) % sliderImages.length);
  };

  return (
    <div style={styles.container}>
      <header style={styles.logoContainer}>
        {/* <h1 style={styles.logo}>GlowNest</h1> */}
        <Navbar/>
      </header>

      <section style={styles.sliderContainer}>
        <img src={sliderImages[currentSlide]} alt="Hero" style={styles.sliderImage} />
        <div style={styles.sliderText}>
          <h2>Reveal Your Natural Radiance</h2>
          <p>Quality skincare for glowing, youthful, confident skin.</p>
        </div>
        <button style={{ ...styles.sliderButton, ...styles.prevButton }} onClick={prevSlide}>
          ‹
        </button>
        <button style={{ ...styles.sliderButton, ...styles.nextButton }} onClick={nextSlide}>
          ›
        </button>
      </section>

      <section>
        <h3 style={styles.sectionTitle}>New Arrivals</h3>
        <div style={styles.grid}>
          {products.map((product, i) => (
            <div key={i} style={styles.card}>
              <img src={product.image} alt={product.name} style={styles.productImage} />
              <h4 style={styles.productName}>{product.name}</h4>
              <p style={styles.price}>{product.price}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;

